{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "**Azure Active Directory**"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "",
                                    "altText": "Pending",
                                    "height": "30px"
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "text": "Conditional Access",
                                    "wrap": true
                                },
                                {
                                    "type": "TextBlock",
                                    "spacing": "Small",
                                    "size": "Small",
                                    "weight": "Bolder",
                                    "color": "Accent",
                                    "text": "Alert"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                           "items": [
                                {
                                    "type": "ActionSet",
                                    "actions": [
                                        {
                                            "type": "Action.OpenUrl",
                                            "title": "BLUEPRINT DEPLOYED!",
                                            "url": "https://portal.azure.com"
                                        }
                                    ]
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                },
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Type",
                            "value": "**Blueprint deployment completed successfully**"
                        },
                        {
                            "title": "Created Date",
                            "value": "@{body('Parse_newly_deployed_conditional_access_policy_details')?['createdDateTime']}"
                        },
                        {
                            "title": "Policy Name",
                            "value": "@{body('Parse_newly_deployed_conditional_access_policy_details')?['displayName']}"
                        },
                        {
                            "title": "Policy ID",
                            "value": "@{body('Parse_newly_deployed_conditional_access_policy_details')?['id']}"
                        },
                       {
                            "title": "Policy State",
                            "value": "@{body('Parse_newly_deployed_conditional_access_policy_details')?['state']}"
                        },
                        {
                            "title": "Message",
                            "value": "Conditional Access policy blueprint was deployed successfully.  Message also reported to secops@contoso.com"
                        }
                    ]
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}

