{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "**Azure Active Directory**"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "",
                                    "altText": "Pending",
                                    "height": "30px"
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "text": "Conditional Access",
                                    "wrap": true
                                },
                                {
                                    "type": "TextBlock",
                                    "spacing": "Small",
                                    "size": "Small",
                                    "weight": "Bolder",
                                    "color": "Accent",
                                    "text": "Alert"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                           "items": [
                                {
                                    "type": "ActionSet",
                                    "actions": [
                                        {
                                            "type": "Action.OpenUrl",
                                            "title": "RESTORE COMPLETED!",
                                            "url": "https://portal.azure.com"
                                        }
                                    ]
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                },
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Type",
                            "value": "**Restore action completed successfully**"
                        },
                        {
                            "title": "Message",
                            "value": "A restore action for Conditional Access policy was completed successfully from the selected backup.  Message also reported to secops@contoso.com"
                        }
                    ]
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}

{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "**Azure Active Directory**"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "",
                                    "altText": "Pending",
                                    "height": "30px"
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "text": "Conditional Access",
                                    "wrap": true
                                },
                                {
                                    "type": "TextBlock",
                                    "spacing": "Small",
                                    "size": "Small",
                                    "weight": "Bolder",
                                    "color": "Accent",
                                    "text": "Alert"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                           "items": [
                                {
                                    "type": "ActionSet",
                                    "actions": [
                                        {
                                            "type": "Action.OpenUrl",
                                            "title": "RESTORE FAILED",
                                            "url": "https://portal.azure.com"
                                        }
                                    ]
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                },
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Type",
                            "value": "**Restore action failed**"
                        },
                        {
                            "title": "Policy Name",
                            "value": "@{body('Parse_the_conditional_access_policy_marked_for_restore')?['displayName']}"
                        },
                        {
                            "title": "Policy ID",
                            "value": "@{body('Parse_the_conditional_access_policy_marked_for_restore')?['id']}"
                        },
                       {
                            "title": "Policy State",
                            "value": "@{body('Parse_the_conditional_access_policy_marked_for_restore')?['state']}"
                        },
                        {
                            "title": "Error message",
                            "value": "@{body('Parse_error_response')?['error']?['message']}"
                        },
                        {
                            "title": "Message",
                            "value": "A restore action for Conditional Access policy has failed. The conditional access policy JSON contains error as specified above.  Message also reported to secops@contoso.com"

                        }
                    ]
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}

{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "**Azure Active Directory**"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "Image",
                                    "url": "https://raw.githubusercontent.com/videor/AutoPilotConditionalAccess/master/AutoPilotConditionalAccess/azure-quickstart-templates/301-conditionalaccess-alerts-automation/images/Alert.PNG",
                                    "altText": "Pending",
                                    "height": "30px"
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "ExtraLarge",
                                    "text": "Conditional Access",
                                    "wrap": true
                                },
                                {
                                    "type": "TextBlock",
                                    "spacing": "Small",
                                    "size": "Small",
                                    "weight": "Bolder",
                                    "color": "Accent",
                                    "text": "Alert"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "ActionSet",
                                    "actions": [
                                        {
                                        }
                                    ]
                                }
                            ],
                            "width": "auto"
                        }
                    ]
                },
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Type",
                            "value": "**Alert: Approval request to restore a conditional access policy from Backup**"
                        },     
                        {
                            "title": "Policy Name",
                            "value": "@{body('Parse_the_conditional_access_policy_marked_for_restore')?['displayName']}"
                        },
                        {
                            "title": "Policy ID",
                            "value": "@{body('Parse_the_conditional_access_policy_marked_for_restore')?['id']}"
                        },
                       {
                            "title": "Policy State",
                            "value": "@{body('Parse_the_conditional_access_policy_marked_for_restore')?['state']}"
                        },
                        {
                            "title": "Message",
                            "value": "A new conditional access policy restore process was initiated. Restore of conditional access policy from Backup requires approval from one of the members in this channel.  Message also reported to secops@contoso.com"
                        }
                    ]
                }
            ]
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "ActionSet",
                    "actions": [
                        {
                            "type": "Action.Submit",
                            "title": "Approve restore of conditional access policy from Backup",
                            "style": "positive",
                            "data": {
                                "id": "_qkQW8dJlUeLVi7ZMEzYVw",
                                "action": "approve"
                            }
                        },
                        {
                            "type": "Action.ShowCard",
                            "title": "Reject restore of conditional access policy",
                            "style": "destructive",
                            "card": {
                                "type": "AdaptiveCard",
                                "body": [
                                    {
                                        "type": "Input.Text",
                                        "id": "RejectCommentID",
                                        "placeholder": "Please specify an appropriate reason for rejecting restore of conditional access policy from Backup.",
                                        "isMultiline": true
                                    }
                                ],
                                "actions": [
                                    {
                                        "type": "Action.Submit",
                                        "title": "Send",
                                        "data": {
                                            "id": "_qkQW8dJlUeLVi7ZMEzYVw",
                                            "action": "reject"
                                        }
                                    }
                                ],
                                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json"
                            }
                        }
                    ]
                }
            ]
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.2",
    "fallbackText": "This card requires Adaptive Cards v1.2 support to be rendered properly."
}
